import React, { useState } from "react";
import "../styles/SignUp.css";
import PersonalDetailsModal from "../components/PersonalDetailsModal";
import InputField from "../components/InputField";
import Button from "../components/Button";
import RoleSelection from "../components/RoleSelection";

const SignUp = () => {
  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    username: "",
    email: "",
    password: "",
    confirmPassword: "",
    universityName: "",
    universityAddress: "",
    semester: "",
    domain: ""
  });
  const [showModal, setShowModal] = useState(false);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setShowModal(true);
  };

  const handleFinalSubmit = async () => {
    try {
      const response = await fetch("http://localhost:5000/signup", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(formData)
      });
      if (response.ok) {
        alert("Signup Successful!");
      } else {
        alert("Signup Failed");
      }
    } catch (error) {
      console.error("Error signing up:", error);
    }
  };

  return (
    <div className="signup-container">
      <div className="signup-left">
        <img src="../assets/signup-illustration.png" alt="Signup Illustration" />
      </div>
      <div className="signup-right">
        <div className="signup-card">
          <h2>Create Your Account</h2>
          {/* <RoleSelection /> */}
          <form onSubmit={handleSubmit} className="signup-form">
            <div className="input-group">
              <InputField type="text" name="firstName" placeholder="First Name" value={formData.firstName} onChange={handleChange} required />
              <InputField type="text" name="lastName" placeholder="Last Name" value={formData.lastName} onChange={handleChange} required />
            </div>
            <InputField type="text" name="username" placeholder="Username" value={formData.username} onChange={handleChange} required />
            <InputField type="email" name="email" placeholder="Email" value={formData.email} onChange={handleChange} required />
            <div className="input-group">
              <InputField type="password" name="password" placeholder="Password" value={formData.password} onChange={handleChange} required />
              <InputField type="password" name="confirmPassword" placeholder="Confirm Password" value={formData.confirmPassword} onChange={handleChange} required />
            </div>
            <Button type="submit" className="signup-button" label="Create Account" />
          </form>
        </div>
      </div>
      {showModal && <PersonalDetailsModal formData={formData} setFormData={setFormData} onSubmit={handleFinalSubmit} onClose={() => setShowModal(false)} />}
    </div>
  );
};

export default SignUp;
