import React from "react";
import "../styles/PersonalDetailsModal.css";
import InputField from "./InputField";
import Button from "./Button";

const PersonalDetailsModal = ({ formData, setFormData, onSubmit, onClose }) => {
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <h2>Complete Your Profile</h2>
        <InputField type="text" name="universityName" placeholder="University Name" value={formData.universityName} onChange={handleChange} required />
        <InputField type="text" name="universityAddress" placeholder="University Address" value={formData.universityAddress} onChange={handleChange} required />
        <InputField type="text" name="semester" placeholder="Semester" value={formData.semester} onChange={handleChange} required />
        <InputField type="text" name="domain" placeholder="Domain" value={formData.domain} onChange={handleChange} required />
        <div className="modal-buttons">
          <Button label="Submit" onClick={onSubmit} />
          <Button label="Cancel" onClick={onClose} />
        </div>
      </div>
    </div>
  );
};

export default PersonalDetailsModal;
