import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import Button from "../components/Button";
import InputField from "../components/InputField";
import logo from "../assets/LOGO.png";
import "../styles/SignUp.css";

const SignUp = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    email: "",
    password: "",
    confirmPassword: "",
    role: ""
  });
  
  const [errors, setErrors] = useState({});
  const [isSubmitting, setIsSubmitting] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
    
    // Clear error when user types
    if (errors[name]) {
      setErrors({
        ...errors,
        [name]: ""
      });
    }
  };

  const validateForm = () => {
    const newErrors = {};
    
    // First Name validation
    if (!formData.firstName.trim()) {
      newErrors.firstName = "First name is required";
    }
    
    // Last Name validation
    if (!formData.lastName.trim()) {
      newErrors.lastName = "Last name is required";
    }
    
    // Email validation
    if (!formData.email.trim()) {
      newErrors.email = "Email is required";
    } else if (!/^\S+@\S+\.\S+$/.test(formData.email)) {
      newErrors.email = "Email is invalid";
    }
    
    // Password validation
    if (!formData.password) {
      newErrors.password = "Password is required";
    } else if (formData.password.length < 6) {
      newErrors.password = "Password must be at least 6 characters";
    }
    
    // Confirm Password validation
    if (formData.password !== formData.confirmPassword) {
      newErrors.confirmPassword = "Passwords do not match";
    }
    
    // Role validation
    if (!formData.role) {
      newErrors.role = "Please select a role";
    }
    
    return newErrors;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    // Validate form
    const formErrors = validateForm();
    if (Object.keys(formErrors).length > 0) {
      setErrors(formErrors);
      return;
    }
    
    setIsSubmitting(true);
    
    try {
      // Here you would typically make an API call to register the user
      // For now, we'll just simulate a successful registration
      
      setTimeout(() => {
        // Redirect to the appropriate dashboard based on role
        if (formData.role === "student") {
          navigate("/StudentDashboard");
        } else {
          navigate("/TeacherDashboard");
        }
        setIsSubmitting(false);
      }, 1000);
      
    } catch (error) {
      setIsSubmitting(false);
      console.error("Registration failed:", error);
      // Handle registration error
    }
  };

  const navigateToSignIn = () => {
    navigate("/SignIn");
  };

  return (
    <div className="signup-container">
      <div className="signup-form-container">
        <div className="logo-container">
          <img src={logo} alt="TALIM Logo" className="signup-logo" />
        </div>
        
        <h1 className="signup-title">Create Your Account</h1>
        
        <form className="signup-form" onSubmit={handleSubmit}>
          <div className="name-fields">
            <div className="firstname-field">
              <InputField
                label="First Name"
                type="text"
                placeholder="John"
                value={formData.firstName}
                onChange={(e) => handleChange({ target: { name: 'firstName', value: e.target.value } })}
                error={errors.firstName}
              />
            </div>
            <div className="lastname-field">
              <InputField
                label="Last Name"
                type="text"
                placeholder="Doe"
                value={formData.lastName}
                onChange={(e) => handleChange({ target: { name: 'lastName', value: e.target.value } })}
                error={errors.lastName}
              />
            </div>
          </div>
          
          <InputField
            label="Email Address"
            type="email"
            placeholder="johndoe@example.com"
            value={formData.email}
            onChange={(e) => handleChange({ target: { name: 'email', value: e.target.value } })}
            error={errors.email}
          />
          
          <InputField
            label="Password"
            type="password"
            placeholder="********"
            value={formData.password}
            onChange={(e) => handleChange({ target: { name: 'password', value: e.target.value } })}
            error={errors.password}
          />
          
          <InputField
            label="Confirm Password"
            type="password"
            placeholder="********"
            value={formData.confirmPassword}
            onChange={(e) => handleChange({ target: { name: 'confirmPassword', value: e.target.value } })}
            error={errors.confirmPassword}
          />
          
          <InputField
            label="Role"
            type="select"
            value={formData.role}
            onChange={(e) => handleChange({ target: { name: 'role', value: e.target.value } })}
            error={errors.role}
            options={[
              { value: "student", label: "Student" },
              { value: "teacher", label: "Teacher" }
            ]}
          />
          
          <Button type="submit" variant="dark" onClick={null}>
            {isSubmitting ? "Creating Account..." : "Sign Up"}
          </Button>
        </form>
        
        <div className="signin-redirect">
          <p>
            Already have an account?{" "}
            <span className="signin-link" onClick={navigateToSignIn}>
              Sign In
            </span>
          </p>
        </div>
      </div>
    </div>
  );
};

export default SignUp;
