import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import InputField from "../components/InputField";
import Button from "../components/Button";
import logo from "../assets/LOGO.png";
import "../styles/SignUp.css";

const SignUp = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    email: "",
    password: "",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("Form data submitted:", formData);
    // Add your signup logic here
    // After successful signup, redirect to appropriate page
    // navigate("/signin");
  };

  const navigateToSignIn = () => {
    navigate("/signin");
  };

  return (
    <div className="signup-container">
      {/* Navigation Bar */}
      <div className="signup-nav">
        <div className="logo-container">
          <img src={logo} alt="E-tutor Logo" className="logo" />
          <span className="logo-text">E-tutor</span>
        </div>
        <div className="nav-buttons">
          <span className="signin-text">Already have an account?</span>
          <Button onClick={navigateToSignIn} variant="light">
            Sign In
          </Button>
        </div>
      </div>

      {/* Main Content */}
      <div className="signup-content">
        {/* Left Illustration */}
        <div className="signup-illustration"></div>

        {/* Right Form */}
        <div className="signup-form-container">
          <form onSubmit={handleSubmit} className="signup-form">
            <h2 className="form-title">Sign Up</h2>
            
            <div className="form-fields">
              <InputField
                label="Email"
                type="email"
                placeholder="Enter your email"
                value={formData.email}
                onChange={(e) => handleChange({ target: { name: "email", value: e.target.value } })}
              />

              <InputField
                label="Password"
                type="password"
                placeholder="Enter your password"
                value={formData.password}
                onChange={(e) => handleChange({ target: { name: "password", value: e.target.value } })}
              />

              <div className="form-options">
                <div className="remember-me">
                  <input type="checkbox" id="rememberMe" />
                  <label htmlFor="rememberMe">Remember me</label>
                </div>
                
                <Button type="submit" variant="dark">
                  Sign Up
                </Button>
              </div>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default SignUp;
