import React, { useState } from "react";
import "../styles/SignUp.css";
import PersonalDetailsModal from "../components/PersonalDetailsModal";

const SignUp = () => {
  const [step, setStep] = useState(1);
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    password: "",
    university: "",
    address: "",
    semester: "",
    domain: "",
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (step === 1) {
      setStep(2);
    } else {
      try {
        const response = await fetch("http://localhost:5000/signup", {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify(formData),
        });
        const data = await response.json();
        console.log("Signup successful", data);
      } catch (error) {
        console.error("Signup failed", error);
      }
    }
  };

  return (
    <div className="signup-container">
      {step === 1 ? (
        <form className="signup-form" onSubmit={handleSubmit}>
          <h2>Create your account</h2>
          <input type="text" name="name" placeholder="Full Name" onChange={handleChange} required />
          <input type="email" name="email" placeholder="Email Address" onChange={handleChange} required />
          <input type="password" name="password" placeholder="Create Password" onChange={handleChange} required />
          <button type="submit">Sign Up</button>
        </form>
      ) : (
        <PersonalDetailsModal formData={formData} handleChange={handleChange} handleSubmit={handleSubmit} />
      )}
    </div>
  );
};

export default SignUp;
