import React, { useState } from 'react';
import { Modal, Button, Form, Nav, Navbar, Container } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import './styles.css';

const SignupLoginPage = () => {
  const [showModal, setShowModal] = useState(false);
  const [isLogin, setIsLogin] = useState(true);
  const [userType, setUserType] = useState('student'); // 'student' or 'teacher'
  
  const handleClose = () => setShowModal(false);
  const handleShow = () => setShowModal(true);
  
  const toggleMode = () => {
    setIsLogin(!isLogin);
  };
  
  return (
    <div className="main-container">
      {/* Navbar with properly aligned logo and sign out button */}
      <Navbar bg="light" expand="lg" className="mb-4">
        <Container>
          <Navbar.Brand href="#" className="me-auto">
            <img 
              src="/logo.png" 
              alt="Logo" 
              height="30" 
              className="d-inline-block align-top"
            />
          </Navbar.Brand>
          <Navbar.Toggle aria-controls="basic-navbar-nav" />
          <Navbar.Collapse id="basic-navbar-nav" className="justify-content-end">
            <Nav>
              <Nav.Link href="#">Home</Nav.Link>
              <Nav.Link href="#">About</Nav.Link>
              <Nav.Link href="#">Contact</Nav.Link>
              <Nav.Link href="#">Sign Out</Nav.Link>
            </Nav>
          </Navbar.Collapse>
        </Container>
      </Navbar>

      <Container className="d-flex flex-column align-items-center justify-content-center min-vh-100">
        <div className="auth-container p-4 rounded shadow">
          <h2 className="text-center mb-4">{isLogin ? 'Login' : 'Sign Up'}</h2>
          
          {/* Toggle buttons for Student/Teacher */}
          {!isLogin && (
            <div className="btn-group mb-4 w-100">
              <Button 
                variant={userType === 'student' ? 'primary' : 'outline-primary'}
                onClick={() => setUserType('student')}
                className="w-50"
              >
                Student
              </Button>
              <Button 
                variant={userType === 'teacher' ? 'primary' : 'outline-primary'}
                onClick={() => setUserType('teacher')}
                className="w-50"
              >
                Teacher
              </Button>
            </div>
          )}
          
          <Form>
            {!isLogin && (
              <Form.Group className="mb-3">
                <Form.Label>Full Name</Form.Label>
                <Form.Control type="text" placeholder="Enter your full name" />
              </Form.Group>
            )}
            
            <Form.Group className="mb-3">
              <Form.Label>Email address</Form.Label>
              <Form.Control type="email" placeholder="Enter email" />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Password</Form.Label>
              <Form.Control type="password" placeholder="Password" />
            </Form.Group>
            
            <Button variant="primary" type="submit" className="w-100 mb-3">
              {isLogin ? 'Login' : 'Sign Up'}
            </Button>
            
            <div className="text-center">
              <Button variant="link" onClick={toggleMode}>
                {isLogin ? "Don't have an account? Sign up" : "Already have an account? Login"}
              </Button>
            </div>
          </Form>
        </div>
      </Container>

      {/* Sign Up Modal */}
      <Modal show={showModal} onHide={handleClose} centered>
        <Modal.Header closeButton>
          <Modal.Title>{userType === 'student' ? 'Student Sign Up' : 'Teacher Sign Up'}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group className="mb-3">
              <Form.Label>Full Name</Form.Label>
              <Form.Control type="text" placeholder="Enter your full name" />
            </Form.Group>
            
            <Form.Group className="mb-3">
              <Form.Label>Email address</Form.Label>
              <Form.Control type="email" placeholder="Enter email" />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Password</Form.Label>
              <Form.Control type="password" placeholder="Password" />
            </Form.Group>
            
            {userType === 'teacher' && (
              <>
                <Form.Group className="mb-3">
                  <Form.Label>Subject</Form.Label>
                  <Form.Control type="text" placeholder="Subject you teach" />
                </Form.Group>
                
                <Form.Group className="mb-3">
                  <Form.Label>School/Institution</Form.Label>
                  <Form.Control type="text" placeholder="Your school or institution" />
                </Form.Group>
              </>
            )}
            
            {userType === 'student' && (
              <>
                <Form.Group className="mb-3">
                  <Form.Label>Grade/Year</Form.Label>
                  <Form.Control type="text" placeholder="Your grade or year" />
                </Form.Group>
                
                <Form.Group className="mb-3">
                  <Form.Label>School</Form.Label>
                  <Form.Control type="text" placeholder="Your school" />
                </Form.Group>
              </>
            )}
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Cancel
          </Button>
          <Button variant="primary" onClick={handleClose}>
            Sign Up
          </Button>
        </Modal.Footer>
      </Modal>

      {/* Button to trigger signup modal */}
      <Button 
        variant="primary" 
        className="position-fixed bottom-0 end-0 m-4"
        onClick={handleShow}
      >
        Sign Up Now
      </Button>
    </div>
  );
};

export default Signup;
